urange (select n_name, sum(l_extendedprice * (1 - l_discount)) as revenue from (((select r_regionkey from region_radb is radb where r_name = 'ASIA') as r1 join (select n_name, n_nationkey, n_regionkey from nation_radb is radb) as n1 on r_regionkey = n_regionkey) join (select * from (((((select o_orderkey, o_custkey from orders_radb is radb where o_orderdate >= '1994-01-01' and o_orderdate < '1995-01-01') as o1 join (select l_orderkey,l_suppkey,l_extendedprice,l_discount from lineitem_radb is radb) as l1 on o_orderkey = l_orderkey) join (select c_custkey, c_nationkey from customer_radb is radb) as c1 on o_custkey = c_custkey) join (select s_suppkey, s_nationkey from supplier_radb is radb) as s1 on l_suppkey = s_suppkey)) where c_nationkey = s_nationkey) as s1 on n_nationkey = s_nationkey) group by n_name);



