urange (select o_orderkey, o_orderdate, o_shippriority from (select * from (select c_custkey from customer_radb is RADB where c_mktsegment = 'BUILDING') join (select o_custkey, o_orderkey, o_orderdate, o_shippriority from orders_radb is RADB where o_orderdate > '1995-03-15') on c_custkey = o_custkey) join (select l_orderkey from lineitem_radb is RADB where l_shipdate < '1995-03-17') on o_orderkey = l_orderkey );