SELECT U1.n_name as n_name,U1.u_n_name as u_n_name,U2.n_name as n_name2,U2.u_n_name as u_n_name2 from ((SELECT n_name,u_n_name, o_orderkey FROM customer c, orders o, nation n WHERE c_nationkey = n_nationkey AND c_custkey = o_custkey AND n_name = 'CHINA' and u_n_name > 0) U1 JOIN (SELECT n_name,u_n_name, o_orderkey FROM supplier s, orders o, lineitem l, nation n WHERE s_nationkey = n_nationkey AND o_orderkey = l_orderkey AND s_suppkey = l_suppkey AND n_name = 'GERMANY' and u_n_name > 0) U2 ON u1.o_orderkey=u2.o_orderkey);